package ch2;

public class SemicolonExam {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 1;	// 변수 x를 선언하고 정수 1을 저장
		/* int x;	x = 1;
		 *  변수 선언 후 나중에 값 저장
		 */
		int y = 2;	// 변수 y를 선언하고 정수 2을 저장
		
		int result = x + y; // 변수 result를 선언하고 변수 x와 y를 더한 값을 저장
		
		//출력 기능의 라이브러리 제공함 - println() 함수(메소드)
		System.out.println(result);
	}

}
